function Ant(x, y, sizeAnts , canvas) {
    const obj = {};
    obj.x = x;
    obj.y = y;
    obj.dx = 0;
    obj.dy = 0;

    obj.antSensitiveRadius = 30;

    // got a piece of food or not
    obj.hasFood  = false;

    // after home or food  is detected -> intensity is 1000
    obj.intensity = 0;


    const ctx = canvas.getContext("2d");
    const ballRadius = sizeAnts;
    const time = 10;// how many iter'ations until it changes it's direction
    let iter = 0 ;// counter of iterations
    let angle = getRandomInt( 0 , 360 );
    const speed = 1.5;
    let lastNextPoint;
    let skipNextPoint = false;

    obj.play = function (nextPoint) {
        //redraw
        ctx.beginPath();
        ctx.arc(obj.x, obj.y, ballRadius, 0, Math.PI*2);
        // paint the ant depending on it's status
        if ( obj.hasFood ) {
            ctx.fillStyle = foodColor;
        } else if ( obj.intensity > 0 ) {
            ctx.fillStyle = homeColor;
        } else {
            ctx.fillStyle = antColor;
        }
        ctx.fill();
        ctx.closePath();

        // if it did certain time of steps:
        if (iter === time) {
            iter = 0;

            if ( obj.intensity > 0 ) {
                obj.leavePoint = true;
                obj.intensity -= 1;
            }

            skipNextPoint = lastNextPoint?.intensity < nextPoint?.intensity;

            // there is a chance that it will change it's path
            const rand = Math.random();

            if (nextPoint === undefined || skipNextPoint) {
                if ( rand < 0.25 ) {
                    angle += 25;
                } else if ( rand > 0.75 ) {
                    angle -= 25;
                }
            }
        }

        //detection if it goes beyond canvas border
        if(obj.x + obj.dx < ballRadius) {
            obj.x = canvas.width-ballRadius-10;
        }
        else if(obj.x + obj.dx > canvas.width-ballRadius) {
            obj.x = ballRadius;
        }

        if(obj.y + obj.dy > canvas.height-ballRadius+2) {
            obj.y = ballRadius;
        }
        else if ( obj.y + obj.dy < ballRadius-2) {
            obj.y = canvas.height-ballRadius;
        }

        let radians;
        // calculate next coordinates depending on direction angle
        if (nextPoint !== undefined && !skipNextPoint) {
            radians = calcAngleDegrees(nextPoint.x, nextPoint.y);
        } else {
            radians = degreesToRadians(angle);
        }
        obj.x = speed * Math.cos(radians) + obj.x;
        obj.y = speed * Math.sin(radians) + obj.y;

        iter += 1;
        skipNextPoint = false;
    };

    function calcAngleDegrees(nextX, nextY) {
        return Math.atan2( nextY - obj.y, nextX - obj.x);
    }

    function degreesToRadians (degrees) {
        return degrees * ( Math.PI / 180 );
    }

    // checks if an ant inside some area or collide with something
    obj.checkCollision = function ( x, y, radius) {
        if ( obj.x < x + radius) { // right
            if ( obj.x > x - radius) { // left
                if ( obj.y < y + radius ) { // bottom
                    if ( obj.y > y - radius ) { // top
                        return true;
                    }
                }
            }
        }
        else {
            return false;
        }
    };

    obj.collisionWithHome = function () {
        obj.intensity = 1000;
        obj.hasFood = false;
    }

    obj.collisionWithFood = function () {
        obj.intensity = 1000;
        obj.hasFood = true;
    }

    return obj;
}



function Food(x, y, sizeFood, canvas) {
    const obj = {};

    obj.x = x;
    obj.y = y;

    const ctx = canvas.getContext("2d");

    obj.play = function () {
        ctx.beginPath();
        ctx.arc(obj.x, obj.y, sizeFood, 0, Math.PI * 2);
        ctx.fillStyle = "#0e6b0e";
        ctx.fill();
        ctx.closePath();
    }

    return obj;
}



function Grid(canvasW, canvasH, size, canvas) {
    const obj = {};

    obj.columns = canvasW / size;
    obj.rows = canvasH / size;
    obj.intensityFall = -1;

    obj.matrix = new Array(obj.columns);

    for (let i = 0; i < obj.columns; i++) {
        obj.matrix[i] = new Array(obj.rows);

        for (let j = 0; j < obj.rows; j++) {
            obj.matrix[i][j] = { foodIntensity: 0, homeIntensity: 0 };
        }
    }

    const ctx = canvas.getContext("2d");

    obj.play = function () {
        obj.matrix.forEach((column, columnIndex) => {
            column.forEach((row, rowIndex) => {
                if (obj.matrix[columnIndex][rowIndex].homeIntensity > 0) {
                    row.homeIntensity += obj.intensityFall;
                    ctx.beginPath();
                    ctx.rect(columnIndex * size, rowIndex * size, size, size);
                    // ${row.foodIntensity}
                    ctx.fillStyle = `rgba(250, 250, 250)`;
                    ctx.fill();
                    ctx.closePath();
                } else if (row.foodIntensity > 0) {
                    row.foodIntensity += obj.intensityFall;
                    ctx.beginPath();
                    ctx.rect(columnIndex * size, rowIndex * size, size, size);
                    // ${row.foodIntensity}
                    ctx.fillStyle = `rgba(250, 250, 0)`;
                    ctx.fill();
                    ctx.closePath();
                }
            });
        });
    }

    obj.addPoint = function (x, y, isFood, intensity) {
        const position = findPosition(x, y);

        if (isFood) {
            obj.matrix[position.column][position.row].foodIntensity = intensity;
        } else {
            obj.matrix[position.column][position.row].homeIntensity = intensity;
        }
    }

    const findPosition = function (x, y) {
        const column = parseInt(x / size);
        const row = parseInt(y / size);

        return {
            column,
            row
        };
    }

    return obj;
}



function Home(x, y, sizeHome, canvas) {
    const obj = {};

    obj.x = x;
    obj.y = y;
    obj.radius = sizeHome;

    const ctx = canvas.getContext("2d");

    obj.play = function () {
        ctx.beginPath();
        ctx.arc(obj.x, obj.y, obj.radius, 0, Math.PI * 2);
        ctx.fillStyle = "#801716";
        ctx.fill();
        ctx.closePath();
    }

    return obj;
}

const foodColor = '#16b116';
const homeColor = '#e12120';
const antColor = '#0095DD';


function Ants() {
    const canvas = document.getElementById("myCanvas");
    const canvasH = canvas.height;
    const canvasW = canvas.width;
    const ctx = canvas.getContext("2d");
    const sizeAnts = 3;
    const sizeHome = 30;
    const sizeFood = 8;

    const howManyFood = 100;
    const food = [];

    const howManyAnts = 200;
    const ants = [];

    const grid = Grid(canvasW, canvasH, 5, canvas);

    // create home
    const home = Home(canvasW/2, canvasH/2, sizeHome, canvas );

    // create ants
    for (let i = 0; i < howManyAnts; i++) {
        const rx = canvasW/2;
        const ry = canvasH/2;

        ants[i] = Ant( rx, ry, sizeAnts, canvas );
    }

    // create food
    for (let k = 0; k < howManyFood; k++) {
        // food[k] = Food( 100, 100, sizeFood, canvas );

        var rx = getRandomInt( 10 , canvasW  - 10 ); // random x
        var ry = getRandomInt( 10 , canvasH - 10 ); // random y
        food[k] = Food( rx, ry, sizeFood, canvas );
    }

    // make everything move
    setInterval(function () {
        // first we clear canvas
        ctx.clearRect(0, 0, canvasW, canvasH);

        //then we draw everything again
        grid.play();
        home.play();

        food.forEach(foodItem => {
            foodItem.play();
        })

        ants.forEach((ant) => {
            // todo replace with a clock
            if (ant.leavePoint) {
                ant.leavePoint = false;
                if (ant.hasFood) {
                    grid.addPoint(ant.x, ant.y, true, ant.intensity);
                } else {
                    grid.addPoint(ant.x, ant.y, false, ant.intensity);
                }
            }

            let nextPoint;

            // find Food
            // find Home with food
            // if (ant.hasFood) {
            //     nextPoint = pathToHome.findBestPointInRadius(ant.x, ant.y, [], home);
            // } else {
            //     nextPoint = pathToFood.findBestPointInRadius(ant.x, ant.y, food);
            // }

            ant.play();

            // home collision
            if ( ant.checkCollision( home.x, home.y, sizeHome ) ) {
                ant.collisionWithHome();
            }

            // food collision
            food.forEach(foodItem => {
                if ( ant.checkCollision( foodItem.x, foodItem.y, sizeFood ) ) {
                    ant.collisionWithFood();
                }
            })
        })
    },40);
}

function getRandomInt(min, max) {
    min = Math.ceil(min);
    max = Math.floor(max);
    return Math.floor(Math.random() * (max - min + 1)) + min; //Максимум и минимум включаются
}

window.addEventListener('DOMContentLoaded', Ants);



function Path(color, canvas) {
    const obj = {};

    const ctx = canvas.getContext("2d");

    obj.intensityFall = -1;

    obj.points = [];
    // point = {
    //     x: 0,
    //     y: 0,
    //     intensity: 0,
    // }

    obj.play = function () {
        obj.points.forEach((point, index) => {
            point.intensity += obj.intensityFall;

            if (point.intensity <= 0) {
                removePoint(index);
            } else {
                ctx.beginPath();
                ctx.arc(point.x, point.y, 1, 0, Math.PI * 2);
                ctx.fillStyle = color;
                // ctx.fillStyle = color + intensityToHex(point.intensity);
                ctx.fill();
                ctx.closePath();
            }
        });
    }

    function removePoint(index) {
        obj.points.splice(index, 1);
    }

    // function normalize(val, minVal, maxVal, newMin, newMax) {
    //     return newMin + (val - minVal) * (newMax - newMin) / (maxVal - minVal);
    // }

    // function intensityToHex(intensity) {
    //     const res = parseInt(normalize(obj.points[0].intensity, 0, 1000, 100, 255)).toString(16)
    //     return res.padStart(2, "0");
    // }

    obj.addPoint = function (x, y, intensity) {
        obj.points.push({
            x: parseInt(x),
            y: parseInt(y),
            intensity
        });
    }

    obj.findBestPointInRadius = function (antX, antY, priorityPoints = [], home ) {
        let bestPoint;
        const r = 80;

        if (home !== undefined) {
            // distance to home = distance to home center - home radius
            const distanceToHome = distance(home.x, home.y, antX, antY) - home?.radius
            if (distanceToHome <= r) {
                return {...home, intensity: 1100};
            }
        } else if (priorityPoints.length) {
            bestPoint = priorityPoints.find(point => {
                return distance(point.x, point.y, antX, antY) <= r
            })

            if (bestPoint !== undefined) {
                bestPoint.intensity = 1100;
                return bestPoint;
            }
        }

        obj.points.forEach(point => {
            if (distance(point.x, point.y, antX, antY) <= r) {
                // if bestPoint exists
                // if no it's the bestPoint
                if (bestPoint === undefined || bestPoint?.intensity > point.intensity) {
                    bestPoint = point;
                }
            }
        })

        return bestPoint;
    }

    function distance (x1, y1, x2, y2) {
        return Math.hypot(x2 - x1, y2 - y1);
    }

    return obj;
}
